import { action } from 'typesafe-actions';
import { TodosTypes, Todo } from './../../../types';

export const loadRequest = () => action(TodosTypes.LOAD_REQUEST);
export const loadSuccess = (data: Todo[]) => action(TodosTypes.LOAD_SUCCCES, { data });
export const loadFailure = () => action(TodosTypes.LOAD_FAILURE);

export const createRequest = (todoDes: string | null) => action(TodosTypes.LOAD_REQUEST, { todoDes });
export const createSuccess = (data: Todo[]) => action(TodosTypes.LOAD_SUCCCES, { data });
export const createFailure = () => action(TodosTypes.LOAD_FAILURE);
