import { call, put } from 'redux-saga/effects';
import api from '../../../../services/api';

import axios from 'axios';
import { loadSuccess, loadFailure, createSuccess, createFailure } from './actions';

export function* load() {
    try {
        const response = yield call(api.get, `/todos/`);
        yield put(loadSuccess(response.data));
    } catch (err) {
        yield put(loadFailure());
    }
}

export function* create(action: any) {

    const response: any = yield axios.post('http://127.0.0.1:8000/api/todos/', { des: action.payload.todoDes })
        .then(function (response) {
            console.log(response);
            return response
        })
        .catch(function (error) {
            console.log(error);
            return response;
        })

    yield put(response.data)


}
