import React, { Component } from 'react'

import { connect } from 'react-redux';
import { bindActionCreators, Dispatch } from 'redux';

import { Todo } from './types';
import { ApplicationState } from '../App/store';

import * as TodosActions from './store/ducks/todos/actions';


interface State {
    todos: Todo[]
}

interface DispatchProps {
    loadRequest(): void
    createRequest(todoDes: string | null): void
}

type Props = State & DispatchProps

const mapStateToProps = (state: ApplicationState) => ({
    todos: state.todos.data,
});

const mapDispatchToProps = (dispatch: Dispatch) => bindActionCreators(TodosActions, dispatch);

class Todos extends Component<Props>{
    state = {
        des: null
    }
    componentDidMount() {
        const { loadRequest } = this.props;
        loadRequest();
    }

    render() {
        const { todos, createRequest } = this.props
        return (<div className="main">
            <div className="jumbotron jumbotron-fluid">
                <ul className="list-group list-group-horizontal">
                    {todos.map(item => <li className="list-group-item">{item.des}</li>)}
                </ul>
            </div>
            <div className="jumbotron jumbotron-fluid">
                <input
                    type="text"
                    name="todo"
                    className="input--todo"

                    onChange={(e) => this.setState({ des: e.target.value })}
                />
                {this.state.des ? <button
                    type="submit"
                    className="button--todo"

                    onClick={() => createRequest(this.state.des)}
                /> : null}
            </div>
        </div>)
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Todos);
