import { Reducer } from 'redux';
import { TodosState, TodosTypes, Todo } from './../../../types';

const INITIAL_STATE: TodosState = {
    data: [],
    error: false,
    loading: false,
};

const reducer: Reducer<TodosState> = (state = INITIAL_STATE, action) => {
    switch (action.type) {
        case TodosTypes.LOAD_REQUEST:
            return { ...state, loading: true };
        case TodosTypes.LOAD_SUCCCES:
            return { ...state, loading: false, error: false, data: action.payload.data, };
        case TodosTypes.LOAD_FAILURE:
            return { ...state, loading: false, error: true, data: [], };

        case TodosTypes.CREATE_REQUEST: {
            return { ...state, loading: true }
        };
        case TodosTypes.CREATE_SUCCCES:
            {
                return {
                    ...state,
                    loading: false,
                    error: false,
                    data: [
                        ...state.data,
                        {
                            id: action.payload.todo.id,
                            des: action.payload.todo.des,
                            status: action.payload.todo.status,
                            created: action.payload.todo.created,
                            modified: action.payload.todo.modified
                        }
                    ],
                }
            };
        case TodosTypes.CREATE_FAILURE:
            return { ...state, loading: false, error: true, data: [], };

        case TodosTypes.DELETE_REQUEST:
            return { ...state, loading: true };
        case TodosTypes.DELETE_SUCCCES:
            return { ...state, loading: false, error: false, data: action.payload.data, };
        case TodosTypes.DELETE_FAILURE:
            return { ...state, loading: false, error: true, data: [], };


        default:
            return state;
    }
};

export default reducer;
